function save_options(){var b=document.getElementById("color").value;var a=document.getElementById("like").checked;chrome.storage.sync.set({favoriteColor:b,likesColor:a},function(){var c=document.getElementById("status");c.textContent="Options saved.";setTimeout(function(){c.textContent=""},750)})}function restore_options(){chrome.storage.sync.get({favoriteColor:"red",likesColor:true},function(a){document.getElementById("color").value=a.favoriteColor;document.getElementById("like").checked=a.likesColor})}document.addEventListener("DOMContentLoaded",restore_options);document.getElementById("save").addEventListener("click",save_options);